function enviar_correo_usuario($respuesta){

    //echo "ENTRO AL METODO enviar_correo_usuario"."<br>";
    //echo "RECIBO LAS VARIABLES:"."<br>";
    //echo "respuesta = ".$respuesta."<br>";

    // Inicio
    $mail = new PHPMailer(true);

    try {
        // Configuracion SMTP
        //$mail->SMTPDebug = 3;
        $mail->isSMTP();                                      // Set mailer to use SMTP
        $mail->SMTPAuth = true;                               // Enable SMTP authentication
        $mail->SMTPOptions = array(
